drop table if exists park_campgrounds;
drop table if exists park_activities;
drop view if exists sumStamps;
drop table if exists park_stamps;
drop table if exists parks;
drop table if exists activities;
drop table if exists campgrounds;
drop table if exists articles;
drop procedure if exists getNumberOfStampsByCode;
drop table if exists user;

create table user (
  username varchar(100) primary key,
  password varchar(100)
);


create table parks (
  park_code varchar(10) primary key,
  park_full_name varchar(255),
  park_url varchar(4000),
  park_description varchar(10000),
  park_location varchar(255),
  CHECK (LENGTH(park_code) = 4)
);







create table park_stamps (
  park_code varchar(10),
  number_of_stamps int,
  foreign key (park_code) references parks(park_code) ON DELETE CASCADE
);






create table campgrounds (
  campgrounds_id varchar (100) primary key,
  latitude float,
  longitude float,
  description varchar(10000),
  name varchar(100)
);


create table park_campgrounds(
park_code varchar(10),
campgrounds_id varchar(100),
foreign key (park_code) references parks(park_code) ON DELETE CASCADE,
foreign key (campgrounds_id) references campgrounds(campgrounds_id) ON DELETE CASCADE
);







create table activities (
  activity_id varchar (100) primary key,
  activity_name varchar (100)
);





create table park_activities (
  park_code varchar (10),
  activity_id varchar(100),
  foreign key (park_code) references parks(park_code) ON DELETE CASCADE,
  foreign key (activity_id) references activities(activity_id) ON DELETE CASCADE
);






create table if not exists articles (
  id varchar (100) primary key,
  url varchar (4000),
  title varchar(1000),
  listingDescription varchar (10000)
);



create view sumStamps as 
select sum(number_of_stamps) as total
from park_stamps;

CREATE PROCEDURE getNumberOfStampsByCode(IN code VARCHAR(10))
BEGIN
    SELECT number_of_stamps 
    FROM park_stamps
    WHERE park_stamps.park_code = code;
END;

CREATE TRIGGER check_park_code_length
  BEFORE INSERT ON parks
  FOR EACH ROW
BEGIN
  IF length(NEW.park_code) != 4 THEN
    SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = 'park_code has to have length 4';
  END IF;
END;